import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {
    const [enteredTitle, setEnteredTitle] = useState("");
    const [enteredAmount, setEnteredAmount] = useState("");
    const [enteredDate, setEnteredDate] = useState("");
    //unique state approach

      const [userInput, setUserInput] = useState({
        enteredTitle: '',
        enteredAmount: '',
         enteredDate: ''
      })


  const titleChangeHander = (event) => {
    setEnteredTitle(event.target.value)
  };

  const amountChangeHander = (event) => {
    setEnteredAmount(event.target.value)
  };

  const dateChangeHander = (event) => {
    setEnteredDate(event.target.value)
  };

  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control"></div>
        <label>Title</label>
        <input type="text" onChange={titleChangeHander} />
      </div>
      <div className="new-expense__controls">
        <div className="new-expense__control"></div>
        <label>Amount</label>
        <input
          type="number"
          min="0.01"
          setp="0.01"
          onChange={amountChangeHander}
        />
      </div>
      <div className="new-expense__controls">
        <div className="new-expense__control"></div>
        <label>Date</label>
        <input
          type="date"
          min="2019-01-01"
          max="2022-12-31"
          onChange={dateChangeHander}
        />
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
